import { nameByRace } from "@src/index";

describe("nameByRace(race, gender?)", () => {
  it("returns a string", () => {
    expect(typeof nameByRace("demon")).toBe("string");
  });

  it("returns an error when no gender is passed for a race that requires one", () => {
    expect(nameByRace("elf")).toEqual(new Error("Must provide a gender!"));
  });
});
